        coms.toLocaleString();
    }

    hanldeCityChange = (city) => {
        let n = city.target.name;
        let v = city.target.value;
        this.setState({[n]: v})
        v = this.upperCaser(v);
        this.allCities(v);
    }

    render(){
        return(
            <div>
                <FetchZips handleZipChange = {this.handleZipChange}/>
                {
                    (<div className = "fetching">
                        <div className = "zipCodeFind"><fieldset className = "box">
                        <b>ZipCode Search</b>
                        {this.state.place.map((item, index) =>
                        <div key = {index}>
                            City: {item.City} <br></br>
                            State: {item.State} <br></br>
                            Population (Estimated): {item.EstimatedPopulation}<br></br>
                            Total Wages: ${item.TotalWages + '.00'}<br></br>
                            Country: {item.Country}<br></br> <br></br>
                        </div>
                        )}
                        </fieldset></div>
                        <br></br><br></br>
                        <div className = "cityFind"><fieldset>


                        </fieldset></div>
                    </div>

                    )
                }
            </div>
        )
    }
}

export default FetchZipsResult;
