import React, {Component} from 'react'
import FetchZips from './fetchZips'

class FetchZipsResult extends Component{
    constructor(){
        super();
        this.state = {
            place: [],
            zipCode: "",
            error: false,
            zips: []
        }
    }
    async componentDidMount() {
        document.getElementById("nonValid").innerHTML = "Find Cities within an entered ZipCode";
        // document.getElementById("nonValidCity").innerHTML = "";
    }
    allZipCodes = async (zipCode) => {
        try{
            const link = "https://ctp-zip-api.herokuapp.com/zip/" + zipCode;
            const result = await fetch(link);
            console.log(result);
            if(result.status !== 200){
                throw new Error("Entered ZipCode is not valid.");
            }
            const res = await result.json();
            this.setState({
                place: res,
                error: false
            })
            document.getElementById("nonValid").innerHTML = "Entered ZipCode is valid.";
            console.log(res);
        }
        catch(error){
            console.log(error);
            this.setState({error: true})
        document.getElementById("nonValid").innerHTML = "You entered an invalid ZipCode.";
        }
    } 
    handleZipChange = (entry) => {
        let n = entry.target.name;
        let v = entry.target.value; 
        this.setState({
            [n]: v
        }) 
        this.allZipCodes(v);
    }
    render(){
        return(
            <div>
                <FetchZips handleZipChange = {this.handleZipChange}/>
                {
                    (<div className = "fetching">
                        <b>ZipCode Search</b>
                        {this.state.place.map((item, index) =>
                        <div key = {index}>
                            City: {item.City} <br></br>
                            State: {item.State} <br></br>
                            Approximate Population: {item.EstimatedPopulation}<br></br>
                            Total Wages: ${item.TotalWages}<br></br>
                            Country: {item.Country}<br></br> <br></br>
                        </div>
                        )}
                    </div>)
                }
            </div>
        )
    }
}

export default FetchZipsResult;
